+$color-skin: rgb(131, 98, 55);
+$color-skin-second: rgb(78, 56, 26);
+$color-wear-back: black;
+$color-wear-front: rgb(31, 29, 29);
+$color-blur: rgba(153, 211, 196, 0.055);
+$color-dec-back: rgb(37, 59, 35);
+$color-dec-front: rgb(77, 34, 32);
+$color-dec-main: rgb(228, 209, 36);
+
+* {
+	box-sizing: border-box;
+	margin: 0;
+	padding: 0;
+}
+
+body {
+	display: flex;
+	justify-content: center;
+	align-items: center;
+	width: 100vw;
+	height: 100vh;
+}
+
+.coord {
+	position: absolute;
+	display: flex;
+}
+
+.hero {
+	flex-direction: column;
+	align-items: center;
+	height: 80vh;
+	width: 60vh;
+}
+
+.hero__head {
+	justify-content: center;
+	top: 10%;
+	background-color: $color-wear-back;
+	width: 20%;
+	height: 13%;
+	border-radius: 40% 40% 60% 60% / 80% 80% 40% 40%;
+}
+
+.hero__head::before,
+.hero__head::after {
+	content: '';
+	position: absolute;
+	top: -45%;
+	background-color: $color-wear-back;
+	height: 110%;
+	width: 20%;
+}
+
+.hero__head::before {
+	left: -1%;
+	border-radius: 80% 15% 0% 0% / 100% 60% 0% 0%;
+}
+
+.hero__head::after {
+	right: 1%;
+	border-radius: 15% 80% 0% 0% / 60% 100% 0% 0%;
+}
+
+.hero__eyes {
+	position: relative;
+	justify-content: center;
+	top: 30%;
+	width: 100%;
+	height: 14%;
+}
+
+.hero__l-eye,
+.hero__r-eye {
+	margin: 0 7%;
+	width: 22%;
+	height: 90%;
+	background-color: $color-skin;
+}
+
+.hero__l-eye {
+	border-radius: 30% 50% 20% 40%;
+}
+
+.hero__r-eye {
+	border-radius: 50% 30% 40% 20%;
+}
+
+.hero__r-eye::before,
+.hero__r-eye::after {
+	content: '';
+	position: absolute;
+	top: 15%;
+	width: 10%;
+	height: 60%;
+	background-color: $color-skin-second;
+	border-radius: 50%;
+}
+.hero__r-eye::before {
+	right: 28%;
+}
+
+.hero__r-eye::after {
+	left: 28%;
+}
+
+.hero__mouth {
+	justify-content: center;
+	top: 55%;
+	width: 50%;
+	height: 20%;
+	border-radius: 40% 40% 30% 30% / 20% 20% 10% 10%;
+	background-color: $color-skin;
+}
+
+.hero__mouth::before {
+	content: '';
+	position: absolute;
+	top: 45%;
+	background-color: $color-wear-back;
+	height: 12%;
+	width: 60%;
+}
+
+.hero__body {
+	justify-content: center;
+	top: 22%;
+	width: 90%;
+	height: 50%;
+}
+
+.hero__cape,
+.hero__cape::before {
+	position: absolute;
+	width: 100%;
+	background-color: $color-wear-back;
+}
+
+.hero__cape {
+	height: 80%;
+	border-radius: 40% 40% 10% 10% / 100% 100% 0 0;
+}
+
+.hero__cape::before {
+	content: '';
+	height: 100%;
+	top: 99%;
+	clip-path: polygon(0% 0%, 100% 0%, 98% 30%, 70% 2%, 55% 30%, 45% 30%, 30% 2%, 2% 30%);
+}
+
+.hero__torso {
+	flex-direction: column;
+	align-items: center;
+	width: 31%;
+	height: 84%;
+	background-color: $color-wear-front;
+	border-radius: 25% 25% 50% 50% / 20% 20% 60% 60%;
+}
+
+.hero__chest::before,
+.hero__chest::after {
+	content: '';
+	position: absolute;
+	top: 20%;
+	width: 50%;
+	height: 10%;
+	border-bottom: 3px solid $color-wear-back;
+}
+
+.hero__chest::before {
+	left: -1%;
+	border-right: 3px solid $color-wear-back;
+	border-radius: 0 0 50% 20% / 0 0 50% 20%;
+}
+
+.hero__chest::after {
+	right: -1%;
+	border-left: 3px solid $color-wear-back;
+	border-radius: 0 0 20% 50% / 0 0 20% 50%;
+}
+
+.hero__belly-top::before,
+.hero__belly-top::after,
+.hero__belly-middle::before,
+.hero__belly-middle::after,
+.hero__belly-bottom::before,
+.hero__belly-bottom::after {
+	content: '';
+	position: absolute;
+	border-bottom: 2px solid $color-wear-back;
+}
+
+.hero__belly-top::before,
+.hero__belly-top::after {
+	top: 40%;
+	width: 30%;
+	height: 6%;
+}
+
+.hero__belly-top::before {
+	left: 19%;
+	border-right: 2px solid $color-wear-back;
+	border-radius: 0 0 50% 20% / 0 0 50% 20%;
+}
+
+.hero__belly-top::after {
+	right: 19%;
+	border-left: 2px solid $color-wear-back;
+	border-radius: 0 0 20% 50% / 0 0 20% 50%;
+}
+
+.hero__belly-middle::before,
+.hero__belly-middle::after {
+	top: 45%;
+	width: 25%;
+	height: 6%;
+}
+
+.hero__belly-middle::before {
+	left: 24%;
+	border-right: 2px solid $color-wear-back;
+	border-radius: 0 0 50% 20% / 0 0 50% 20%;
+}
+
+.hero__belly-middle::after {
+	right: 24%;
+	border-left: 2px solid $color-wear-back;
+	border-radius: 0 0 20% 50% / 0 0 20% 50%;
+}
+
+.hero__belly-bottom::before,
+.hero__belly-bottom::after {
+	top: 50%;
+	width: 20%;
+	height: 6%;
+}
+
+.hero__belly-bottom::before {
+	left: 29%;
+	border-right: 2px solid $color-wear-back;
+	border-radius: 0 0 50% 20% / 0 0 50% 20%;
+}
+
+.hero__belly-bottom::after {
+	right: 29%;
+	border-left: 2px solid $color-wear-back;
+	border-radius: 0 0 20% 50% / 0 0 20% 50%;
+}
+
+.hero__belt {
+	position: absolute;
+	justify-content: center;
+	align-items: center;
+	top: 65%;
+	width: 90%;
+	height: 10%;
+	background-color: $color-dec-back;
+	border-radius: 10%;
+	z-index: 5;
+}
+
+.hero__belt__badge {
+	display: flex;
+	justify-content: center;
+	align-items: center;
+	height: 160%;
+	width: 50%;
+	background-color: $color-dec-front;
+	border-radius: 50%;
+
+	.ti {
+		font-size: 5vh;
+		color: $color-dec-main;
+	}
+}
+
+.hero__l-hand,
+.hero__r-hand {
+	position: absolute;
+	top: -1%;
+	width: 10%;
+	height: 65%;
+	background-color: $color-wear-front;
+}
+
+.hero__l-hand {
+	left: 23%;
+	border-radius: 50% 10% 40% 40% / 40% 10% 50% 50%;
+	transform: rotate(23deg);
+}
+
+.hero__r-hand {
+	right: 23%;
+	border-radius: 10% 50% 40% 40% / 10% 40% 50% 50%;
+	transform: rotate(-23deg);
+}
+
+.hero__l-hand::before,
+.hero__r-hand::before {
+	content: '';
+	position: absolute;
+	background-color: $color-skin;
+	top: 95%;
+	width: 65%;
+	height: 20%;
+}
+
+.hero__l-hand::before {
+	left: 17%;
+	clip-path: polygon(
+		20% 100%,
+		0 50%,
+		0 15%,
+		10% 0,
+		90% 0,
+		100% 15%,
+		100% 50%,
+		80% 75%,
+		65% 75%,
+		70% 50%,
+		50% 30%,
+		30% 50%,
+		30% 100%
+	);
+}
+
+.hero__r-hand::before {
+	right: 17%;
+	clip-path: polygon(
+		20% 75%,
+		0 50%,
+		0 15%,
+		10% 0,
+		90% 0,
+		100% 15%,
+		100% 50%,
+		80% 100%,
+		70% 100%,
+		70% 50%,
+		50% 30%,
+		30% 50%,
+		35% 75%
+	);
+}
+
+.hero__legs {
+	position: absolute;
+	background: $color-wear-front;
+	top: 53%;
+	width: 41%;
+	height: 50%;
+	clip-path: polygon(20% 0, 80% 0, 100% 100%, 80% 100%, 50% 25%, 20% 100%, 0 100%);
+}
